
public class SwitchWithString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String choice = "thee";
		
		switch(choice)
		{
		case "one":
			System.out.println("one");
			break;
			
		case "two":
			System.out.println("two");
			break;
			
		case "three":
			System.out.println("three");
			break;
			
		default:
			System.out.println("Invalid");
		}

	}

}
